import React, { useState, useRef, useEffect } from 'react';
import { useChat } from '../api';
import './Chat.css';

const Chat: React.FC = () => {
  const { messages, isLoading, error, sendMessage, clearHistory } = useChat();
  const [inputMessage, setInputMessage] = useState('');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  // Ëá™Âä®ÊªöÂä®Âà∞Â∫ïÈÉ®
  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  // ÂèëÈÄÅÊ∂àÊÅØÂ§ÑÁêÜ
  const handleSendMessage = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!inputMessage.trim() || isLoading) return;

    const messageToSend = inputMessage.trim();
    setInputMessage('');
    await sendMessage(messageToSend);
  };

  // Ê∏ÖÁ©∫ÂéÜÂè≤Â§ÑÁêÜ
  const handleClearHistory = async () => {
    if (window.confirm('Á°ÆÂÆöË¶ÅÊ∏ÖÁ©∫ÊâÄÊúâËÅäÂ§©ËÆ∞ÂΩïÂêóÔºü')) {
      await clearHistory();
    }
  };

  // Ê†ºÂºèÂåñÊó∂Èó¥
  const formatTime = (timestamp: string) => {
    return new Date(timestamp).toLocaleTimeString('zh-CN', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  return (
    <div className="chat-container">
      <div className="chat-header">
        <h1>üí¨ AI ÂØπËØù</h1>
        <div className="chat-subtitle">‰∏éAIÂä©ÊâãÊô∫ËÉΩÂØπËØù</div>
        <div className="chat-actions">
          <button
            onClick={handleClearHistory}
            className="clear-btn"
            disabled={isLoading}
          >
            üóëÔ∏è Ê∏ÖÁ©∫ÂéÜÂè≤
          </button>
        </div>
      </div>

      <div className="chat-messages">
        {messages.length === 0 && !isLoading && (
          <div className="empty-state">
            <div className="empty-icon">üí≠</div>
            <p>ÂºÄÂßã‰∏éAIÂä©ÊâãÂØπËØùÂêßÔºÅ</p>
          </div>
        )}

        {messages.map((message) => (
          <div
            key={message.id}
            className={`message ${message.role === 'user' ? 'user-message' : 'ai-message'}`}
          >
            <div className="message-avatar">
              {message.role === 'user' ? 'üë§' : 'ü§ñ'}
            </div>
            <div className="message-content">
              <div className="message-text">{message.content}</div>
              <div className="message-time">{formatTime(message.timestamp)}</div>
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="message ai-message loading">
            <div className="message-avatar">ü§ñ</div>
            <div className="message-content">
              <div className="typing-indicator">
                <span></span>
                <span></span>
                <span></span>
              </div>
            </div>
          </div>
        )}

        <div ref={messagesEndRef} />
      </div>

      {error && (
        <div className="error-message">
          ‚ùå {error}
        </div>
      )}

      <form onSubmit={handleSendMessage} className="chat-input-form">
        <div className="input-container">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            placeholder="ËæìÂÖ•ÊÇ®ÁöÑÈóÆÈ¢ò..."
            disabled={isLoading}
            className="message-input"
          />
          <button
            type="submit"
            disabled={!inputMessage.trim() || isLoading}
            className="send-btn"
          >
            {isLoading ? '‚è≥' : 'üì§'}
          </button>
        </div>
      </form>
    </div>
  );
};

export default Chat;
