import React, { useState, useEffect } from 'react';
import { Row, Col, Card, Statistic, Button, Progress, Space, Typography } from 'antd';
import {
  ClockCircleOutlined,
  CodeOutlined,
  ProjectOutlined,
  ThunderboltOutlined,
  ReloadOutlined,
  BarChartOutlined,
  SettingOutlined,
  MessageOutlined
} from '@ant-design/icons';
import './Dashboard.css';

const { Title, Text } = Typography;

const Dashboard: React.FC = () => {
  const [greeting, setGreeting] = useState('');
  const [currentTime, setCurrentTime] = useState('');
  const [workHours, setWorkHours] = useState(0);
  const [stats, setStats] = useState({
    commits: 3,
    insertions: 245,
    deletions: 12,
    projects: 26,
    productivity: 85
  });

  useEffect(() => {
    updateGreeting();
    updateWorkHours();
    loadStats();

    // ÊØèÂàÜÈíüÊõ¥Êñ∞‰∏ÄÊ¨°Êó∂Èó¥
    const interval = setInterval(() => {
      updateGreeting();
      updateWorkHours();
    }, 60000);

    return () => clearInterval(interval);
  }, []);

  const updateGreeting = () => {
    const hour = new Date().getHours();
    let greetingText = hour < 6 ? 'üåô Ê∑±Â§ú‰∫Ü' : hour < 9 ? 'üåÖ Êó©ÂÆâ' : hour < 12 ? '‚òÄÔ∏è ‰∏äÂçàÂ•Ω' : hour < 14 ? 'üå§Ô∏è ‰∏≠ÂçàÂ•Ω' : hour < 18 ? 'üåÜ ‰∏ãÂçàÂ•Ω' : hour < 22 ? 'üåÉ Êôö‰∏äÂ•Ω' : 'üåô Â§úÊ∑±‰∫Ü';
    setGreeting(greetingText);

    const now = new Date();
    setCurrentTime(now.toLocaleDateString('zh-CN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      weekday: 'long'
    }) + ' ' + now.toLocaleTimeString('zh-CN'));
  };

  const updateWorkHours = () => {
    const now = new Date();
    const currentHour = now.getHours();
    const currentMinute = now.getMinutes();
    let hours = 0;

    // Â∑•‰ΩúÊó∂Èó¥: 9:30-12:30 (3h) + 14:00-18:30 (4.5h)
    if (currentHour > 9 || (currentHour === 9 && currentMinute >= 30)) {
      if (currentHour < 12 || (currentHour === 12 && currentMinute <= 30)) {
        // ‰∏äÂçàÂ∑•‰ΩúÊó∂Èó¥
        const morningMinutes = (currentHour - 9) * 60 + currentMinute - 30;
        hours = Math.max(0, morningMinutes / 60);
      } else if (currentHour >= 12 && currentHour < 14) {
        // Âçà‰ºëÊó∂Èó¥ 12:30-14:00ÔºåÊòæÁ§∫‰∏äÂçàÁöÑ3Â∞èÊó∂
        hours = 3;
      } else if (currentHour >= 14) {
        hours = 3; // ‰∏äÂçà3Â∞èÊó∂
        if (currentHour < 18 || (currentHour === 18 && currentMinute <= 30)) {
          // ‰∏ãÂçàÂ∑•‰ΩúÊó∂Èó¥
          const afternoonMinutes = (currentHour - 14) * 60 + currentMinute;
          hours += afternoonMinutes / 60;
        } else {
          hours += 4.5; // ‰∏ãÂçà4.5Â∞èÊó∂
        }
      }
    }

    setWorkHours(hours);
  };

  const loadStats = () => {
    // ËøôÈáåÂèØ‰ª•Ë∞ÉÁî®APIËé∑ÂèñÁúüÂÆûÊï∞ÊçÆ
    // ÊöÇÊó∂‰ΩøÁî®Ê®°ÊãüÊï∞ÊçÆ
    setStats({
      commits: Math.floor(Math.random() * 10) + 1,
      insertions: Math.floor(Math.random() * 500) + 100,
      deletions: Math.floor(Math.random() * 50) + 5,
      projects: 26,
      productivity: Math.floor(Math.random() * 30) + 70
    });
  };

  const weeklyData = [120, 200, 150, 80, 70, 180, 250]; // Ê®°ÊãüÂë®Êï∞ÊçÆ

  return (
    <div className="dashboard-container">
      {/* Â§¥ÈÉ®ÈóÆÂÄô */}
      <div className="dashboard-header">
        <div className="greeting-section">
          <Title level={1}>ü§ñ AI ÁßÅ‰∫∫Âä©ÁêÜ</Title>
          <Text className="subtitle">ÊÇ®ÁöÑÊô∫ËÉΩÂºÄÂèë‰ºô‰º¥ v1.6.0</Text>
        </div>
        <div className="time-section">
          <Text strong className="greeting-text">{greeting}ÔºåÁñØÁãÇÁöÑÁü≥Â§¥ÔºÅ</Text>
          <br />
          <Text type="secondary">{currentTime}</Text>
        </div>
      </div>

      {/* ‰ªäÊó•ÁªüËÆ° */}
      <Card
        title={
          <Space>
            <BarChartOutlined />
            ‰ªäÊó•Â∑•‰ΩúÁªüËÆ°
            <Button
              type="text"
              icon={<ReloadOutlined />}
              onClick={loadStats}
              size="small"
            >
              Âà∑Êñ∞
            </Button>
          </Space>
        }
        className="stats-section"
      >
        <Row gutter={[16, 16]}>
          <Col xs={24} sm={12} md={8} lg={6}>
            <Card className="stat-card">
              <Statistic
                title="‰ªäÊó•Êèê‰∫§"
                value={stats.commits}
                prefix={<CodeOutlined />}
                valueStyle={{ color: '#3f8600' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={8} lg={6}>
            <Card className="stat-card">
              <Statistic
                title="Êñ∞Â¢û‰ª£Á†ÅË°å"
                value={stats.insertions}
                prefix="‚ûï"
                valueStyle={{ color: '#1890ff' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={8} lg={6}>
            <Card className="stat-card">
              <Statistic
                title="Âà†Èô§‰ª£Á†ÅË°å"
                value={stats.deletions}
                prefix="‚ûñ"
                valueStyle={{ color: '#cf1322' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={8} lg={6}>
            <Card className="stat-card">
              <Statistic
                title="Â∑•‰ΩúÊó∂Èïø"
                value={workHours.toFixed(1)}
                suffix="h"
                prefix={<ClockCircleOutlined />}
                valueStyle={{ color: '#722ed1' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={8} lg={6}>
            <Card className="stat-card">
              <Statistic
                title="È°πÁõÆÊÄªÊï∞"
                value={stats.projects}
                prefix={<ProjectOutlined />}
                valueStyle={{ color: '#13c2c2' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={8} lg={6}>
            <Card className="stat-card">
              <Statistic
                title="Áîü‰∫ßÂäõ"
                value={stats.productivity}
                suffix="%"
                prefix={<ThunderboltOutlined />}
                valueStyle={{ color: '#fa8c16' }}
              />
              <Progress
                percent={stats.productivity}
                showInfo={false}
                strokeColor="#fa8c16"
                size="small"
              />
            </Card>
          </Col>
        </Row>
      </Card>

      {/* Êú¨Âë®Ë∂ãÂäø */}
      <Card
        title={
          <Space>
            üìà Êú¨Âë®‰ª£Á†ÅË∂ãÂäø
          </Space>
        }
        className="chart-section"
      >
        <div className="weekly-chart">
          {weeklyData.map((value, index) => {
            const days = ['Âë®‰∏Ä', 'Âë®‰∫å', 'Âë®‰∏â', 'Âë®Âõõ', 'Âë®‰∫î', 'Âë®ÂÖ≠', 'Âë®Êó•'];
            const maxValue = Math.max(...weeklyData);
            const height = (value / maxValue) * 100;

            return (
              <div key={index} className="chart-bar-container">
                <div className="chart-bar" style={{ height: `${height}%` }}>
                  <span className="chart-value">{value}</span>
                </div>
                <span className="chart-label">{days[index]}</span>
              </div>
            );
          })}
        </div>
      </Card>

      {/* Âø´ÈÄüÊìç‰Ωú */}
      <Card title="üöÄ Âø´ÈÄüÊìç‰Ωú" className="actions-section">
        <Space wrap size="large">
          <Button
            type="primary"
            size="large"
            icon={<BarChartOutlined />}
            onClick={() => window.location.hash = '#statistics'}
          >
            Êü•ÁúãÁªüËÆ°
          </Button>
          <Button
            type="primary"
            size="large"
            icon={<ProjectOutlined />}
            onClick={() => window.location.hash = '#projects'}
          >
            ÁÆ°ÁêÜÈ°πÁõÆ
          </Button>
          <Button
            type="primary"
            size="large"
            icon={<MessageOutlined />}
            onClick={() => window.location.hash = '#chat'}
          >
            AIÂØπËØù
          </Button>
          <Button
            type="default"
            size="large"
            icon={<SettingOutlined />}
            onClick={() => window.location.hash = '#settings'}
          >
            Á≥ªÁªüËÆæÁΩÆ
          </Button>
        </Space>
      </Card>
    </div>
  );
};

export default Dashboard;
