import React, { useState, useEffect } from 'react';
import {
  Row, Col, Card, Statistic, Button, List, Avatar, Space, Typography,
  Tag, message, Modal, Select, Input, Form, Divider, Tooltip
} from 'antd';
import {
  FolderOpenOutlined,
  FireOutlined,
  CheckCircleOutlined,
  PauseCircleOutlined,
  GlobalOutlined,
  SettingOutlined,
  MobileOutlined,
  DownOutlined,
  UpOutlined,
  BuildOutlined,
  CloudUploadOutlined,
  ReloadOutlined,
  PlusOutlined
} from '@ant-design/icons';
import { useProjects } from '../api';
import './Projects.css';

const { Title, Text } = Typography;
const { Option } = Select;

interface Project {
  name: string;
  path: string;
  lastCommitTime?: string;
  status?: {
    modified: number;
    added: number;
    deleted: number;
  };
}

const Projects: React.FC = () => {
  const { projects, isLoading, error, loadProjects, scanProjects } = useProjects();
  const [recentProjects, setRecentProjects] = useState<Project[]>([]);
  const [selectedProject, setSelectedProject] = useState<string>('');
  const [buildModalVisible, setBuildModalVisible] = useState(false);
  const [uploadModalVisible, setUploadModalVisible] = useState(false);

  // È°πÁõÆÁªüËÆ°Êï∞ÊçÆ
  const projectStats = {
    total: 26,
    active: 12,
    completed: 8,
    paused: 6
  };

  // È°πÁõÆÂàÜÁ±ªÊï∞ÊçÆ
  const projectCategories = [
    { type: 'frontend', name: 'ÂâçÁ´ØÈ°πÁõÆ', count: 15, icon: <GlobalOutlined />, color: '#1890ff' },
    { type: 'backend', name: 'ÂêéÁ´ØÈ°πÁõÆ', count: 8, icon: <SettingOutlined />, color: '#52c41a' },
    { type: 'mobile', name: 'ÁßªÂä®Á´Ø', count: 3, icon: <MobileOutlined />, color: '#fa8c16' }
  ];

  useEffect(() => {
    loadProjects();
  }, [loadProjects]);

  useEffect(() => {
    if (projects && projects.length > 0) {
      // ÊåâÊúÄÂêéÊèê‰∫§Êó∂Èó¥ÊéíÂ∫èÔºåÂèñÊúÄËøë6‰∏™
      const sorted = [...projects].sort((a: any, b: any) =>
        new Date(b.lastCommitTime || 0).getTime() - new Date(a.lastCommitTime || 0).getTime()
      );
      setRecentProjects(sorted.slice(0, 6));
    }
  }, [projects]);

  // Ê†ºÂºèÂåñÁõ∏ÂØπÊó∂Èó¥
  const formatRelativeTime = (dateString: string) => {
    if (!dateString) return 'Êú™Áü•';
    const date = new Date(dateString);
    const now = new Date();
    const diffMs = now.getTime() - date.getTime();
    const diffHours = Math.floor(diffMs / (1000 * 60 * 60));
    const diffDays = Math.floor(diffHours / 24);

    if (diffHours < 1) return 'ÂàöÂàö';
    if (diffHours < 24) return `${diffHours}Â∞èÊó∂Ââç`;
    if (diffDays < 7) return `${diffDays}Â§©Ââç`;
    return date.toLocaleDateString('zh-CN');
  };

  // Â§ÑÁêÜÈ°πÁõÆÊìç‰Ωú
  const handleGitPull = async (projectName: string) => {
    message.info(`Ê≠£Âú®ÊãâÂèñÈ°πÁõÆ: ${projectName}`);
    // ËøôÈáåÂèØ‰ª•Ë∞ÉÁî®API
  };

  const handleGitPush = async (projectName: string) => {
    message.info(`Ê≠£Âú®Êé®ÈÄÅÈ°πÁõÆ: ${projectName}`);
    // ËøôÈáåÂèØ‰ª•Ë∞ÉÁî®API
  };

  const handleBuild = (projectName: string) => {
    setSelectedProject(projectName);
    setBuildModalVisible(true);
  };

  const handleUpload = (projectName: string) => {
    setSelectedProject(projectName);
    setUploadModalVisible(true);
  };

  const handleScanProjects = async () => {
    await scanProjects();
    message.success('È°πÁõÆÊâ´ÊèèÂÆåÊàê');
  };

  return (
    <div className="projects-container">
      <div className="projects-header">
        <Title level={1}>üíº È°πÁõÆÁÆ°ÁêÜ</Title>
        <Text className="projects-subtitle">ÁÆ°ÁêÜÊÇ®ÁöÑÂºÄÂèëÈ°πÁõÆ</Text>
        <Space>
          <Button
            type="primary"
            icon={<ReloadOutlined />}
            onClick={handleScanProjects}
            loading={isLoading}
          >
            Êâ´ÊèèÈ°πÁõÆ
          </Button>
          <Button type="default" icon={<PlusOutlined />}>
            Êñ∞Âª∫È°πÁõÆ
          </Button>
        </Space>
      </div>

      {/* È°πÁõÆÁªüËÆ° */}
      <Card className="stats-section">
        <Row gutter={[16, 16]}>
          <Col xs={24} sm={12} md={6}>
            <Card className="stat-card">
              <Statistic
                title="È°πÁõÆÊÄªÊï∞"
                value={projectStats.total}
                prefix={<FolderOpenOutlined />}
                valueStyle={{ color: '#1890ff' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card className="stat-card">
              <Statistic
                title="Ê¥ªË∑ÉÈ°πÁõÆ"
                value={projectStats.active}
                prefix={<FireOutlined />}
                valueStyle={{ color: '#fa8c16' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card className="stat-card">
              <Statistic
                title="Â∑≤ÂÆåÊàê"
                value={projectStats.completed}
                prefix={<CheckCircleOutlined />}
                valueStyle={{ color: '#52c41a' }}
              />
            </Card>
          </Col>
          <Col xs={24} sm={12} md={6}>
            <Card className="stat-card">
              <Statistic
                title="Â∑≤ÊöÇÂÅú"
                value={projectStats.paused}
                prefix={<PauseCircleOutlined />}
                valueStyle={{ color: '#bfbfbf' }}
              />
            </Card>
          </Col>
        </Row>
      </Card>

      <Row gutter={[24, 24]}>
        <Col xs={24} lg={16}>
          {/* ÊúÄËøëÈ°πÁõÆ */}
          <Card
            title={
              <Space>
                <FireOutlined />
                ÊúÄËøëÈ°πÁõÆ
              </Space>
            }
            className="recent-projects-section"
          >
            <List
              loading={isLoading}
              dataSource={recentProjects}
              renderItem={(project: Project) => (
                <List.Item
                  actions={[
                    <Tooltip title="Git Pull">
                      <Button
                        type="text"
                        icon={<DownOutlined />}
                        onClick={() => handleGitPull(project.name)}
                      />
                    </Tooltip>,
                    <Tooltip title="Git Push">
                      <Button
                        type="text"
                        icon={<UpOutlined />}
                        onClick={() => handleGitPush(project.name)}
                      />
                    </Tooltip>,
                    <Tooltip title="ÊûÑÂª∫È°πÁõÆ">
                      <Button
                        type="text"
                        icon={<BuildOutlined />}
                        onClick={() => handleBuild(project.name)}
                      />
                    </Tooltip>,
                    <Tooltip title="‰∏ä‰º†Âà∞OSS">
                      <Button
                        type="text"
                        icon={<CloudUploadOutlined />}
                        onClick={() => handleUpload(project.name)}
                      />
                    </Tooltip>
                  ]}
                >
                  <List.Item.Meta
                    avatar={<Avatar icon={<FolderOpenOutlined />} />}
                    title={<strong>{project.name}</strong>}
                    description={
                      <Space direction="vertical" size="small">
                        <Text type="secondary">
                          ÊúÄÂêéÊõ¥Êñ∞: {formatRelativeTime(project.lastCommitTime || '')}
                        </Text>
                        {project.status && (
                          <Space size="small">
                            {project.status.modified > 0 && (
                              <Tag color="orange">üìù {project.status.modified} Â∑≤‰øÆÊîπ</Tag>
                            )}
                            {project.status.added > 0 && (
                              <Tag color="green">‚ûï {project.status.added} Â∑≤Ê∑ªÂä†</Tag>
                            )}
                            {project.status.deleted > 0 && (
                              <Tag color="red">‚ûñ {project.status.deleted} Â∑≤Âà†Èô§</Tag>
                            )}
                            {project.status.modified === 0 && project.status.added === 0 && project.status.deleted === 0 && (
                              <Tag color="default">‚úÖ Êó†ÂèòÂåñ</Tag>
                            )}
                          </Space>
                        )}
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
            {recentProjects.length === 0 && !isLoading && (
              <div className="empty-state">
                <Text type="secondary">ÊöÇÊó†È°πÁõÆÊï∞ÊçÆ</Text>
              </div>
            )}
          </Card>
        </Col>

        <Col xs={24} lg={8}>
          {/* È°πÁõÆÂàÜÁ±ª */}
          <Card title="üìÇ È°πÁõÆÂàÜÁ±ª" className="categories-section">
            <List
              dataSource={projectCategories}
              renderItem={(category) => (
                <List.Item>
                  <Card
                    className="category-card"
                    style={{ borderLeft: `4px solid ${category.color}` }}
                  >
                    <Space>
                      <Avatar
                        icon={category.icon}
                        style={{ backgroundColor: category.color }}
                      />
                      <div>
                        <div style={{ fontWeight: 'bold' }}>{category.name}</div>
                        <Text type="secondary">{category.count} ‰∏™È°πÁõÆ</Text>
                      </div>
                    </Space>
                  </Card>
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>

      {/* ÊûÑÂª∫ÈÄâÈ°πÂºπÁ™ó */}
      <Modal
        title={`ÊûÑÂª∫È°πÁõÆ: ${selectedProject}`}
        open={buildModalVisible}
        onCancel={() => setBuildModalVisible(false)}
        footer={[
          <Button key="cancel" onClick={() => setBuildModalVisible(false)}>
            ÂèñÊ∂à
          </Button>,
          <Button key="build" type="primary">
            ÂºÄÂßãÊûÑÂª∫
          </Button>
        ]}
      >
        <Form layout="vertical">
          <Form.Item label="ÊûÑÂª∫Á±ªÂûã" required>
            <Select defaultValue="development">
              <Option value="development">ÂºÄÂèëÁéØÂ¢É</Option>
              <Option value="production">Áîü‰∫ßÁéØÂ¢É</Option>
              <Option value="test">ÊµãËØïÁéØÂ¢É</Option>
            </Select>
          </Form.Item>
          <Form.Item label="ÊûÑÂª∫ÈÄâÈ°π">
            <Space direction="vertical">
              <label>
                <input type="checkbox" defaultChecked /> Ê∏ÖÁêÜÊûÑÂª∫ÁõÆÂΩï
              </label>
              <label>
                <input type="checkbox" defaultChecked /> ÁîüÊàêÊ∫êÁ†ÅÊò†Â∞Ñ
              </label>
              <label>
                <input type="checkbox" /> ÂéãÁº©‰ª£Á†Å
              </label>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* ‰∏ä‰º†ÈÄâÈ°πÂºπÁ™ó */}
      <Modal
        title={`‰∏ä‰º†Âà∞OSS: ${selectedProject}`}
        open={uploadModalVisible}
        onCancel={() => setUploadModalVisible(false)}
        footer={[
          <Button key="cancel" onClick={() => setUploadModalVisible(false)}>
            ÂèñÊ∂à
          </Button>,
          <Button key="upload" type="primary">
            ÂºÄÂßã‰∏ä‰º†
          </Button>
        ]}
      >
        <Form layout="vertical">
          <Form.Item label="OSSÈÖçÁΩÆ" required>
            <Select placeholder="ÈÄâÊã©OSSÈÖçÁΩÆ">
              <Option value="dev">ÂºÄÂèëÁéØÂ¢É</Option>
              <Option value="test">ÊµãËØïÁéØÂ¢É</Option>
              <Option value="prod">Áîü‰∫ßÁéØÂ¢É</Option>
            </Select>
          </Form.Item>
          <Form.Item label="‰∏ä‰º†Ë∑ØÂæÑ">
            <Input placeholder="‰æãÂ¶Ç: /web/projects/" />
          </Form.Item>
          <Form.Item label="Êñá‰ª∂Á±ªÂûã">
            <Select mode="multiple" placeholder="ÈÄâÊã©Ë¶Å‰∏ä‰º†ÁöÑÊñá‰ª∂Á±ªÂûã">
              <Option value="html">HTMLÊñá‰ª∂</Option>
              <Option value="js">JavaScriptÊñá‰ª∂</Option>
              <Option value="css">CSSÊñá‰ª∂</Option>
              <Option value="images">ÂõæÁâáÊñá‰ª∂</Option>
              <Option value="all">ÂÖ®ÈÉ®Êñá‰ª∂</Option>
            </Select>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default Projects;
